import 'package:firebase_analytics/firebase_analytics.dart';abstract class AnalyticsSenderEvents {  Future<void> sendScreen(String name);  Future<void> onEvent(final String event, {Map<String, Object?>? parameters = null});}class AnalyticsSenderEventsImp extends AnalyticsSenderEvents {  final FirebaseAnalytics _analytics;  AnalyticsSenderEventsImp(this._analytics);  @override  Future<void> onEvent(final String event, {Map<String, Object?>? parameters = null}) async {    await _analytics.logEvent(name: event, parameters: parameters);  }  @override  Future<void> sendScreen(final String name) async {    _analytics.setCurrentScreen(screenName: name);  }}