import 'package:path/path.dart';import 'package:sqflite/sqflite.dart';import 'package:whatsappsendnumber/core/db/data_base/%20migration/migration_7.dart';import 'package:whatsappsendnumber/core/db/data_base/%20migration/migration_six.dart';import 'package:whatsappsendnumber/core/db/data_base/tables.dart';class DataBaseLocal {  late Database _database;  Database get() => _database;  DataBaseLocal() {    initDataBase();  }  initDataBase() async {    _database = await createDatabase();  }  Future<Database> createDatabase() async {    var databasesPath = await getDatabasesPath();    String path = join(databasesPath, dataBaseName);    return await openDatabase(path, version: 7,        onCreate: (Database db, int version) async {      await db.execute('CREATE TABLE IF NOT EXISTS $numberPhoneTable '          '($numberPhoneIdentifier INTEGER PRIMARY KEY AUTOINCREMENT, '          '$numberPhoneNumber INTEGER, '          '$numberPhoneMessage TEXT, '          '$numberPhoneDate TEXT)');      await db.execute('CREATE TABLE IF NOT EXISTS $countryTable '          '($countryIdentifier INTEGER PRIMARY KEY AUTOINCREMENT, '          '$countryName TEXT, '          '$countryIso2 TEXT, '          '$countryIso3 TEXT, '          '$countryLada TEXT, '          '$countrySelected BOOL)');      await db.execute('CREATE TABLE IF NOT EXISTS $messageShortTable '          '($messageShortIdentifier INTEGER PRIMARY KEY AUTOINCREMENT, '          '$messageShortMessage TEXT)');    }, onUpgrade: (Database db, int oldVersion, int newVersion) async {      if (newVersion == 6) {        MigrationSix(db);      }      if (newVersion == 7) {        Migration7(db);      }    });  }}