import 'package:path/path.dart';import 'package:sqflite/sqflite.dart';import 'package:whatsappsendnumber/core/db/data_base/tables.dart';class DataBaseLocal {  late Database _database;  Database get() => _database;  DataBaseLocal() {    initDataBase();  }  initDataBase() async {    _database = await createDatabase();  }  Future<Database> createDatabase() async {    var databasesPath = await getDatabasesPath();    String path = join(databasesPath, dataBaseName);    return await openDatabase(path, version: 6, onCreate: (Database db, int version) async {      if(version < 5) {        await db.execute('CREATE TABLE IF NOT EXISTS $numberPhoneTable '            '($numberPhoneIdentifier INTEGER PRIMARY KEY AUTOINCREMENT, '            '$numberPhoneNumber INTEGER, '            '$numberPhoneDate TEXT)');      } else {        await db.execute('CREATE TABLE IF NOT EXISTS $numberPhoneTable '            '($numberPhoneIdentifier INTEGER PRIMARY KEY AUTOINCREMENT, '            '$numberPhoneNumber INTEGER, '            '$numberPhoneMessage TEXT, '            '$numberPhoneDate TEXT)');      }      await db.execute('CREATE TABLE IF NOT EXISTS $countryTable '          '($countryIdentifier INTEGER PRIMARY KEY AUTOINCREMENT, '          '$countryName TEXT, '          '$countryIso2 TEXT, '          '$countryIso3 TEXT, '          '$countryLada TEXT, '          '$countrySelected BOOL)');      await db.execute('CREATE TABLE IF NOT EXISTS $messageShortTable '          '($messageShortIdentifier INTEGER PRIMARY KEY AUTOINCREMENT, '          '$messageShortMessage TEXT)');    }, onUpgrade: (Database db, int oldVersion, int newVersion) async {      if (oldVersion < 5) {        await db.execute('ALTER TABLE $numberPhoneTable ADD COLUMN $numberPhoneMessage TEXT');        await db.execute('DELETE FROM $numberPhoneTable');        await db.execute('DELETE FROM $countryTable');        await db.execute('DELETE FROM $messageShortTable');      }    });  }}