import 'package:whatsappsendnumber/core/db/data_base/data_base.dart';import 'package:whatsappsendnumber/core/db/data_base/tables.dart';import 'package:whatsappsendnumber/features/start_chat/framework/data/config/response/message_short_dto.dart';class MessageShortImpl {  final DataBaseLocal _dataBaseLocal;  MessageShortImpl(this._dataBaseLocal);  Future<MessageShortDto> getMessageShort() async {    final db = await _dataBaseLocal.createDatabase();    final maps = await db.query(messageShortTable);    return maps.isNotEmpty        ? MessageShortDto(int.parse(maps[0][messageShortIdentifier].toString()), maps[0][messageShortMessage].toString())        : MessageShortDto(0, "");  }  Future<void> setMessageShort(MessageShortDto messageShortDto) async {    final db = await _dataBaseLocal.createDatabase();    if (messageShortDto.identifier == 0) {      final insertRows = await db.insert(messageShortTable, {messageShortMessage: messageShortDto.message});      print("insertRows -> $insertRows");    } else {      final updateRows = await db.update(messageShortTable, {messageShortMessage: messageShortDto.message},          where: "$messageShortIdentifier = ?", whereArgs: [messageShortDto.identifier]);      print("messageShortDto.identifier -> ${messageShortDto.identifier}");      print("updateRows -> $updateRows");    }  }}