import 'package:whatsappsendnumber/core/db/data_base/data_base.dart';import 'package:whatsappsendnumber/core/db/data_base/tables.dart';import 'package:whatsappsendnumber/features/history/domain/models/phone_number.dart';import 'package:whatsappsendnumber/features/history/domain/models/phone_number_output.dart';import 'package:whatsappsendnumber/features/history/framework/data/config/models/number_phone_local.dart';class NumbersPhoneImpl {  final DataBaseLocal _dataBaseLocal;  NumbersPhoneImpl(this._dataBaseLocal);  Future<void> deleteAllPhoneNumber() async {    final db = await _dataBaseLocal.createDatabase();    db.delete(numberPhoneTable);  }  Future<int> deletePhoneNumber(int identifier) async {    final db = await _dataBaseLocal.createDatabase();    return await db.delete(numberPhoneTable, where: "$numberPhoneIdentifier = ?", whereArgs: [identifier]);  }  Future<PhoneNumberOutPut> getPhoneNumber() async {    final db = await _dataBaseLocal.createDatabase();    final List<Map<String, dynamic>> maps = await db.query(numberPhoneTable, orderBy: "$numberPhoneIdentifier DESC");    final phoneNumbers = List.generate(maps.length, (counter) {      return PhoneNumber(        identifier: int.parse(maps[counter][numberPhoneIdentifier].toString()),        number: maps[counter][numberPhoneNumber].toString(),        message: maps[counter][numberPhoneMessage].toString(),        date: maps[counter][numberPhoneDate].toString(),      );    });    return PhoneNumberOutPut(phoneNumbers);  }  Future<void> insertPhoneNumber(PhoneNumberLocal numberPhoneLocal) async {    final db = await _dataBaseLocal.createDatabase();    await db.insert(numberPhoneTable, numberPhoneLocal.toMap());  }}