import 'package:whatsappsendnumber/features/home/data/numbers_send/data_source/numbers_local_data_source.dart';import 'package:whatsappsendnumber/features/home/domain/models/number_phone.dart';import 'package:whatsappsendnumber/resources/models/response/result.dart';class NumberPhoneRepository{  final NumbersPhoneDataSource _numbersPhoneDataSource;  NumberPhoneRepository(this._numbersPhoneDataSource);  Future<Result<bool>> saveNumberPhone(NumberPhone numberPhone) => _numbersPhoneDataSource.saveNumberPhone(numberPhone);  Future<Result<List<NumberPhone>>> getNumbersPhone()  => _numbersPhoneDataSource.getNumbersPhone();  Future<Result<bool>> deleteNumberPhone(NumberPhone numberPhone)  => _numbersPhoneDataSource.deleteNumberPhone(numberPhone);  Future<Result<bool>> deleteNumbersPhone(List<NumberPhone> numbersPhone) => _numbersPhoneDataSource.deleteNumbersPhone(numbersPhone);}