import 'package:flutter/widgets.dart';import 'package:whatsappsendnumber/features/home/data/data_source/countries_local_data_source.dart';import 'package:whatsappsendnumber/features/home/data/data_source/countries_remote_data_source.dart';import 'package:whatsappsendnumber/features/home/data/repository/mapper/map_countries_response_to_country_dto.dart';class CountriesRemoteRepository {  final CountriesRemoteDataSource _countriesRemoteDataSource;  final CountriesLocalDataSource _countriesLocalDataSource;  final MapCountriesResponseToCountryDto _mapCountriesResponseToCountryDto;  CountriesRemoteRepository(this._countriesRemoteDataSource, this._countriesLocalDataSource, this._mapCountriesResponseToCountryDto);  Future<bool> getCountriesDownload(BuildContext context) async {    final localCountries = await _countriesLocalDataSource.getCountries();    if (localCountries.isEmpty) {      final countriesRemote = await _countriesRemoteDataSource.getCountriesDownload(context);      final countriesDtos = countriesRemote.map((e) => _mapCountriesResponseToCountryDto.map(e)).toList();      final result = await _countriesLocalDataSource.insertCountries(countriesDtos);      return result;    } else {      return false;    }  }}