import 'dart:convert';import 'package:flutter/widgets.dart';import 'package:whatsappsendnumber/features/home/data/data_source/countries_remote_data_source.dart';import 'package:whatsappsendnumber/features/home/framework/data/config/response/CountriesResponse.dart';class CountriesRemoteDataSourceImpl extends CountriesRemoteDataSource {  final valueJson = "assets/countries.json";  CountriesRemoteDataSourceImpl();  @override  Future<List<CountriesResponse>> getCountriesDownload(BuildContext context) async {    try {      final countriesJson = await DefaultAssetBundle.of(context).loadString(valueJson);      final jsonResult = jsonDecode(countriesJson);      final list = List<CountriesResponse>.from(jsonResult.map((model) => CountriesResponse.fromJson(model)));      return list;    } catch (ex) {      return [];    }  }}