import 'package:flutter/material.dart';import 'package:flutter/services.dart';import 'package:flutter/widgets.dart';import 'package:url_launcher/url_launcher.dart';import 'package:whatsappsendnumber/resources/dimens_app.dart';import 'package:whatsappsendnumber/resources/extensions/extension_context.dart';import 'package:whatsappsendnumber/resources/strings_app.dart';class SendMessagePage extends StatelessWidget {  final _formKey = GlobalKey<FormState>();  final _phoneNumberController = TextEditingController();  SendMessagePage({super.key});  @override  Widget build(BuildContext context) {    return Center(      child: Column(        mainAxisAlignment: MainAxisAlignment.center,        children: [          Container(            margin: const EdgeInsets.symmetric(horizontal: doubleSeparation),            width: double.infinity,            child: Form(                key: _formKey,                child: TextFormField(                  controller: _phoneNumberController,                  keyboardType: TextInputType.phone,                  textAlign: TextAlign.start,                  textInputAction: TextInputAction.done,                  maxLength: 10,                  maxLines: 1,                  inputFormatters: <TextInputFormatter>[FilteringTextInputFormatter.digitsOnly],                  validator: (value) {                    if (value == null || value.length < 10) {                      return context.strings().sendMessagePageInputPhoneError;                    }                    return null;                  },                  decoration: InputDecoration(                    counter: const Offstage(),                    hintText: context.strings().sendMessagePageInputPhone,                    border: OutlineInputBorder(                      borderRadius: BorderRadius.circular(radiusBig),                    ),                  ),                )),          ),          const SizedBox(height: doubleSeparation),          Container(            margin: const EdgeInsets.symmetric(horizontal: doubleSeparation),            width: double.infinity,            child: ElevatedButton(              onPressed: () {                if (_formKey.currentState!.validate()) {                  launchUrl(Uri(scheme: urlScheme, host: urlApiWhatsapp, path: "$urlApiWhatsapp${_phoneNumberController.text}"));                }              },              child: Text(context.strings().sendMessagePageButtonSendStartChat),            ),          ),        ],      ),    );  }}