import 'package:flutter_riverpod/flutter_riverpod.dart';import 'package:whatsappsendnumber/core/db/features/number_phone_data_base/usescase/delete_item_phone_number_uses_case.dart';import 'package:whatsappsendnumber/core/db/features/number_phone_data_base/usescase/get_phone_number_uses_case.dart';import 'package:whatsappsendnumber/features/home/framework/presentation/provider/mapper/map_phone_number_local_to_phone_number_state.dart';import 'package:whatsappsendnumber/features/home/framework/presentation/ui_models/history_state.dart';import 'package:whatsappsendnumber/features/home/framework/presentation/ui_models/home_state.dart';class HistoryProvider extends StateNotifier<HistoryState> {  final GetPhoneNumberUsesCases _getPhoneNumberUsesCases;  final DeleteItemPhoneNumberUsesCases _deleteItemPhoneNumberUsesCases;  final MapPhoneNumberLocalToPhoneNumberState _mapPhoneNumberLocalToPhoneNumberState;  HistoryProvider(this._getPhoneNumberUsesCases, this._deleteItemPhoneNumberUsesCases, this._mapPhoneNumberLocalToPhoneNumberState) : super(const HistoryState()){    getPhoneNumbers();  }  Future<void> getPhoneNumbers() async {    final phoneNumberOutput = await _getPhoneNumberUsesCases.invoke();    state = state.copyWith(numberPhones: phoneNumberOutput.phoneNumbers.map((e) => _mapPhoneNumberLocalToPhoneNumberState.map(e)).toList());  }  Future<void> deleteNumber(int number) async {    await _deleteItemPhoneNumberUsesCases.invoke(number);    getPhoneNumbers();  }}