import 'package:flutter_riverpod/flutter_riverpod.dart';import 'package:whatsappsendnumber/features/splash/framework/page/models/splash_state.dart';import 'package:whatsappsendnumber/features/splash/framework/provider/splash_provider.dart';import 'package:whatsappsendnumber/features/splash/usescases/get_flow_after_splash.dart';final getFlowAfterSplashProvider = Provider<GetFlowAfterSplash>((provider) => GetFlowAfterSplash());final splashProvider = StateNotifierProvider.autoDispose<SplashProvider, SplashState>((provider) {  return SplashProvider(provider.read(getFlowAfterSplashProvider));});