import 'package:flutter_riverpod/flutter_riverpod.dart';import 'package:whatsappsendnumber/features/splash/framework/page/models/splash_state.dart';import 'package:whatsappsendnumber/features/splash/usescases/get_flow_after_splash.dart';class SplashProvider extends StateNotifier<SplashState> {  final GetFlowAfterSplash getFlowAfterSplash;  SplashProvider(this.getFlowAfterSplash) : super(const SplashState());  void setUp() {  }  void getNextFlow(){    Future.delayed(const Duration(seconds: 5)).whenComplete(() {      final response = getFlowAfterSplash.invoke();      state = state.copyWith(navigation: response);    });  }}