import 'dart:io';import 'package:device_info_plus/device_info_plus.dart';class GetDataUserUsesCases {  Future<String?> invoke() async {    if (Platform.isAndroid) {      return await DeviceInfoPlugin().androidInfo.then((item) {        return "${item.device}_${item.id}";      });    } else if (Platform.isIOS) {      return await DeviceInfoPlugin().iosInfo.then((item) {        return "${item.name}_${item.identifierForVendor}";      });    }  }}