import 'package:whatsappsendnumber/features/start_chat/data/data_source/country_data_source.dart';import 'package:whatsappsendnumber/features/start_chat/domain/models/country_model.dart';class CountryRepository {  final CountryDataSource _countriesDataSource;  CountryRepository(this._countriesDataSource);  Future<CountryModel> getCountryCurrent() => _countriesDataSource.getCountryCurrent();  Future<List<CountryModel>> getCountries() => _countriesDataSource.getCountries();  Future<void> updateCurrentCountry(int identifier) => _countriesDataSource.updateCurrentCountry(identifier);}