import 'package:whatsappsendnumber/core/db/features/country_data_base/data_base/country_daba_base_impl.dart';import 'package:whatsappsendnumber/features/start_chat/data/data_source/country_data_source.dart';import 'package:whatsappsendnumber/features/start_chat/domain/models/country_model.dart';import 'package:whatsappsendnumber/features/start_chat/framework/data/implementation/data_source/mapper/map_country_dto_to_country_model.dart';class CountryDataSourceImpl extends CountryDataSource {  final CountryDataBaseImpl countryDataBase;  final MapCountryDtoToCountryModel _mapCountryDtoToCountryModel;  CountryDataSourceImpl(this.countryDataBase, this._mapCountryDtoToCountryModel);  @override  Future<CountryModel> getCountryCurrent() async {    final country = await countryDataBase.getCountryCurrent();    return _mapCountryDtoToCountryModel.map(country);  }  @override  Future<List<CountryModel>> getCountries() async {    final countries = await countryDataBase.getAllCountries();    return countries.map((item) => _mapCountryDtoToCountryModel.map(item)).toList();  }  @override  Future<void> updateCurrentCountry(int identifier) async {    await countryDataBase.updateCountryCurrent(identifier);  }}