import 'package:whatsappsendnumber/core/db/features/message_short/data_base/message_short_daba_base_impl.dart';import 'package:whatsappsendnumber/features/start_chat/data/data_source/message_short_data_source.dart';import 'package:whatsappsendnumber/features/start_chat/domain/models/message_short_model.dart';import 'package:whatsappsendnumber/features/start_chat/framework/data/config/response/message_short_dto.dart';import 'package:whatsappsendnumber/features/start_chat/framework/data/implementation/data_source/mapper/map_message_short_dto_to_message_short_model.dart';class MessageShortDataSourceImpl extends MessageShortDataSource {  final MessageShortImpl _messageShortImpl;  final MapMessageShortDtoToMessageShortModel _mapMessageShortDtoToMessageShortModel;  MessageShortDataSourceImpl(this._messageShortImpl, this._mapMessageShortDtoToMessageShortModel);  @override  Future<MessageShortModel> getMessageShort() async {    final message = await _messageShortImpl.getMessageShort();    return _mapMessageShortDtoToMessageShortModel.map(message);  }  @override  Future<void> updateMessageShort(MessageShortModel messageModel) async {    await _messageShortImpl.setMessageShort(MessageShortDto(messageModel.identifier, messageModel.message));  }}