import 'package:flutter_riverpod/flutter_riverpod.dart';import 'package:whatsappsendnumber/core/db/features/country_data_base/data_base/country_daba_base_impl.dart';import 'package:whatsappsendnumber/core/db/features/number_phone_data_base/framework/di/notifications_di.dart';import 'package:whatsappsendnumber/features/splash/framework/di/splash_di.dart';import 'package:whatsappsendnumber/features/start_chat/data/data_source/country_data_source.dart';import 'package:whatsappsendnumber/features/start_chat/data/repository/country_repository.dart';import 'package:whatsappsendnumber/features/start_chat/framework/data/implementation/data_source/country_data_source_impl.dart';import 'package:whatsappsendnumber/features/start_chat/framework/data/implementation/data_source/mapper/map_country_dto_to_country_model.dart';import 'package:whatsappsendnumber/features/start_chat/framework/presentation/page/ui_models/start_chat_state.dart';import 'package:whatsappsendnumber/features/start_chat/framework/presentation/provider/mapper/map_country_model_to_country_state.dart';import 'package:whatsappsendnumber/features/start_chat/framework/presentation/provider/start_chat_provider.dart';import 'package:whatsappsendnumber/features/start_chat/use_cases/get_countries_use_cases.dart';import 'package:whatsappsendnumber/features/start_chat/use_cases/get_country_current_use_cases.dart';import 'package:whatsappsendnumber/features/start_chat/use_cases/update_country_current_use_cases.dart';final countryDataBaseImplProvider = Provider<CountryDataBaseImpl>((provider) => CountryDataBaseImpl(provider.read(dataBaseProvider)));final mapCountryDtoToCountryModelProvider = Provider<MapCountryDtoToCountryModel>((provider) => MapCountryDtoToCountryModel());final countriesDataSourceImplProvider = Provider<CountryDataSource>(    (provider) => CountryDataSourceImpl(provider.read(countryDataBaseImplProvider), provider.read(mapCountryDtoToCountryModelProvider)));final countryRepositoryProvider = Provider<CountryRepository>((provider) => CountryRepository(provider.read(countriesDataSourceImplProvider)));final getCountryCurrentUseCasesProvider =    Provider<GetCountryCurrentUseCases>((provider) => GetCountryCurrentUseCases(provider.read(countryRepositoryProvider)));final getCountriesUseCasesProvider = Provider<GetCountriesUseCases>((provider) => GetCountriesUseCases(provider.read(countryRepositoryProvider)));final updateCountryCurrentUseCasesProvider =    Provider<UpdateCountryCurrentUseCases>((provider) => UpdateCountryCurrentUseCases(provider.read(countryRepositoryProvider)));final mapCountryModelCountryStateProvider = Provider<MapCountryModelCountryState>((provider) => MapCountryModelCountryState());final startChatProvider = StateNotifierProvider.autoDispose<StartChatProvider, StartChatState>((provider) {  return StartChatProvider(      provider.read(savePhoneNumberUsesCasesProvider),      provider.read(analyticsSenderEventsProvider),      provider.read(getCountryCurrentUseCasesProvider),      provider.read(mapCountryModelCountryStateProvider),      provider.read(getCountriesUseCasesProvider),      provider.read(updateCountryCurrentUseCasesProvider));});