import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:flutter_gen/gen_l10n/app_localizations.dart';extension CustomContext on BuildContext {  AppLocalizations strings() => AppLocalizations.of(this);  void showSnackBar(Text text) {    ScaffoldMessenger.of(this).showSnackBar(SnackBar(      content: text,    ));  }  Color? background() => AppBarTheme.of(this).backgroundColor;  Color? secondary() => Theme.of(this).colorScheme.secondary;  Color? primaryColor() => Theme.of(this).primaryColor;  Color? dividerColor() => Theme.of(this).dividerColor;  Color? errorColor() => Theme.of(this).errorColor;}