import 'package:whatsappsendnumber/resources/models/view_status_model/empty_ui.dart';import 'package:whatsappsendnumber/resources/models/view_status_model/error_ui.dart';import 'package:whatsappsendnumber/resources/models/view_status_model/loading_ui.dart';abstract class ViewEvent {}class EventLoading with ViewEvent {  final LoadingUi? loadingUi;  const EventLoading({this.loadingUi});}class EventSuccess<T> with ViewEvent {  final T data;  const EventSuccess(this.data);}class EventError with ViewEvent {  final ErrorUi? errorUi;  const EventError({this.errorUi});}class EventEmpty with ViewEvent {  final EmptyUi? emptyUi;  const EventEmpty({this.emptyUi});}